function toggle_eqp(o){$.ajax({url:"/equipaments/toggle_eqp",data:{id:o},type:"POST",success:function(o){1==o.active?exibirMsg("Equipamento ativado com sucesso."):exibirMsg("Equipamento desativado com sucesso."),window.location.reload()},error:function(){console.log("errors"),exibirErro("Ocorreu algum erro.")}})}$(document).ready(function(){$("#lab_id").on("change",function(){console.log("onchange"),$.getJSON("/equipaments/equipaments_labs?lab="+$("#lab_id").val(),function(o){exibirMsg("Filtro aplicado"),console.log(o)})})});