function create_escola(){$.ajax({url:"/escolas/nova_escola/",data:{nome:$("#modalNewEscola #nome").val(),endereco:$("#modalNewEscola #endereco").val(),numero:$("#modalNewEscola #numero").val(),bairro:$("#modalNewEscola #bairro").val(),cidade:$("#modalNewEscola #cidade").val(),responsavel:$("#modalNewEscola #responsavel").val(),telefone:$("#modalNewEscola #telefone").val()},type:"GET",success:function(){$("#modalNewEscola").modal("hide"),console.log("salvou"),exibirMsg("Agenda salva com sucesso.")},error:function(){console.log("errors")}})}function open_modal_edit_escola(a){$.getJSON("/escolas/"+a,function(a){$("#modalEditEscola #escola_id").val(a.id),$("#modalEditEscola #nome").val(a.nome),$("#modalEditEscola #endereco").val(a.endereco),$("#modalEditEscola #numero").val(a.numero),$("#modalEditEscola #bairro").val(a.bairro),$("#modalEditEscola #cidade").val(a.cidade),$("#modalEditEscola #responsavel").val(a.responsavel),$("#modalEditEscola #telefone").val(a.telefone)}),$("#modalEditEscola").modal("show")}function editar_escola(){$.ajax({url:"/escolas/atualizar_escola/",data:{id:$("#modalEditEscola #escola_id").val(),nome:$("#modalEditEscola #nome").val(),endereco:$("#modalEditEscola #endereco").val(),numero:$("#modalEditEscola #numero").val(),bairro:$("#modalEditEscola #bairro").val(),cidade:$("#modalEditEscola #cidade").val(),responsavel:$("#modalEditEscola #responsavel").val(),telefone:$("#modalEditEscola #telefone").val()},type:"GET",success:function(){$("#modalEditEscola").modal("hide"),console.log("salvou"),exibirMsg("escola editada")}})}$(document).ready(function(){$("#btn_nova_escola").click(function(){$("#modalNewEscola").modal("show")}),$("#btn_create_escola").click(function(){create_escola()}),$("#btn_editar_escola").click(function(){editar_escola()})});