function open_modal_edit_lab(o){$.getJSON("/laboratorys/get_laboratory?id="+o,function(o){$("#modalEditLaboratory #id").val(o.id),$("#modalEditLaboratory #name").val(o.name),$("#modalEditLaboratory #local").val(o.local),$("#modalEditLaboratory #description").val(o.description)}),$("#modalEditLaboratory").modal("show")}function toggle_lab(o){$.ajax({url:"/laboratorys/toggle_lab",data:{id:o},type:"POST",success:function(o){1==o.active?exibirMsg("Laborat\xf3rio ativado com sucesso."):exibirMsg("Laborat\xf3rio desativado com sucesso."),window.location.reload()},error:function(){console.log("errors"),exibirErro("Ocorreu algum erro.")}})}$(document).ready(function(){$("#btn_new_lab").click(function(){$("#modalNewLaboratory").modal("show")})});